import React, { useState, useRef } from 'react';
import { motion } from 'motion/react';
import './TiltedCard.css';

const TiltedCard = ({
  imageSrc,
  altText = '',
  captionText = '',
  containerHeight = '300px',
  containerWidth = '300px',
  imageHeight = '300px',
  imageWidth = '300px',
  rotateAmplitude = 12,
  scaleOnHover = 1.2,
  showMobileWarning = false,
  showTooltip = true,
  displayOverlayContent = true,
  overlayContent = null
}) => {
  const [isHovered, setIsHovered] = useState(false);
  const [rotateX, setRotateX] = useState(0);
  const [rotateY, setRotateY] = useState(0);
  const cardRef = useRef(null);

  const handleMouseMove = (e) => {
    if (!cardRef.current) return;

    const rect = cardRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;

    const mouseX = e.clientX - centerX;
    const mouseY = e.clientY - centerY;

    const rotateXValue = (mouseY / (rect.height / 2)) * rotateAmplitude;
    const rotateYValue = (mouseX / (rect.width / 2)) * rotateAmplitude;

    setRotateX(-rotateXValue);
    setRotateY(rotateYValue);
  };

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setRotateX(0);
    setRotateY(0);
  };

  return (
    <div className="tilted-card-container">
      {showMobileWarning && (
        <div className="mobile-warning">
          <p>Para melhor experiÃªncia, use um dispositivo com mouse</p>
        </div>
      )}

      <motion.div
        ref={cardRef}
        className="tilted-card"
        style={{
          height: containerHeight,
          width: containerWidth,
        }}
        onMouseMove={handleMouseMove}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        animate={{
          rotateX: rotateX,
          rotateY: rotateY,
          scale: isHovered ? scaleOnHover : 1,
        }}
        transition={{
          type: "spring",
          stiffness: 300,
          damping: 30,
        }}
      >
        <div className="tilted-card-inner">
          <img
            src={imageSrc}
            alt={altText}
            className="tilted-card-image"
            style={{
              height: imageHeight,
              width: imageWidth,
            }}
          />

          {displayOverlayContent && (
            <motion.div
              className="tilted-card-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: isHovered ? 1 : 0 }}
              transition={{ duration: 0.3 }}
            >
              {overlayContent || (
                <div className="tilted-card-default-overlay">
                  <h3>{captionText}</h3>
                </div>
              )}
            </motion.div>
          )}
        </div>

        {showTooltip && isHovered && (
          <motion.div
            className="tilted-card-tooltip"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.2 }}
          >
            {captionText}
          </motion.div>
        )}
      </motion.div>
    </div>
  );
};

export default TiltedCard;
