import React, { useRef } from 'react';
import CardNav from './components/CardNav/CardNav';
import DarkVeil from './components/DarkVeil/DarkVeil';
import MagicBento from './MagicBento';
import VariableProximity from './VariableProximity';
import TextType from './TextType';
import AnimatedContent from './AnimatedContent';
import SpotlightCard from './SpotlightCard';

import './App.css';

// Dados de navegação para o CardNav



const navigationItems = [
  {
    label: 'Serviços',
    bgColor: 'rgba(22, 33, 62, 0.3)',
    textColor: '#ffffff',
    links: [
      { label: 'Nossos Serviços', href: '#services', ariaLabel: 'Seção de Serviços' },
      { label: 'Business Management', href: '#services', ariaLabel: 'Business Management Services' },
      { label: 'Legal Services', href: '#services', ariaLabel: 'Legal Services' }
    ]
  },
  {
    label: 'Contato',
    bgColor: 'rgba(15, 52, 96, 0.3)',
    textColor: '#ffffff',
    links: [
      { label: 'Entre em Contato', href: '#contact', ariaLabel: 'Seção de Contato' },
      { label: 'E-mail', href: '#contact', ariaLabel: 'Contato por E-mail' },
      { label: 'Instagram', href: '#contact', ariaLabel: 'Contato via Instagram' },
      { label: 'Telefone', href: '#contact', ariaLabel: 'Contato por Telefone' }
    ]
  },
  {
    label: 'Sobre',
    bgColor: 'rgba(83, 52, 131, 0.3)',
    textColor: '#ffffff',
    links: [
      { label: 'Nossa História', href: '#services', ariaLabel: 'Sobre a BASE' },
      { label: 'Nossa Missão', href: '#services', ariaLabel: 'Nossa Missão' },
      { label: 'Valores', href: '#services', ariaLabel: 'Nossos Valores' }
    ]
  }
];

function App() {
  const heroContainerRef = useRef(null);
  const servicesContainerRef = useRef(null);

  return (
    <div className="App">
      {/* Background animado */}
      <div className="background-container" style={{ width: '100%', height: '100vh', position: 'fixed', top: 0, left: 0, zIndex: -1 }}>
        <DarkVeil />
      </div>

      {/* Navegação */}
      <CardNav 
        items={navigationItems}
        logo="BASE"
        ctaText="Fale Conosco"
        ctaLink="#contact"
      />

      {/* Seção Hero */}
      <section className="hero-section" ref={heroContainerRef}>
        <div className="hero-content">
          <h1 className="hero-title">
            <VariableProximity 
              label="BASE"
              containerRef={heroContainerRef}
              radius={120}
              falloff="exponential"
              fromFontVariationSettings="'wght' 300, 'opsz' 14"
              toFontVariationSettings="'wght' 900, 'opsz' 48"
            />
          </h1>
          <h2 className="hero-subtitle">Business Agency of Sports & Entertainment</h2>
          <div className="hero-description">
            <TextType 
              text={[
                "BASE is a consulting and legal services firm focused on the sports and entertainment industries.",
                "We deliver integrated solutions in corporate management and legal advisory.",
                "Serving clubs, federations, investors, and entertainment companies.",
                "Our expertise combines professionalism, innovation, and a strategic approach."
              ]}
              typingSpeed={50}
              pauseDuration={2000}
              showCursor={true}
              cursorCharacter="|"
            />
          </div>
        </div>
      </section>

      {/* Seção de Serviços */}
      <section className="services-section" id="services" ref={servicesContainerRef}>
        <div className="section-header">
          <h2 className="section-title">
            <VariableProximity 
              label=""
              containerRef={servicesContainerRef}
              radius={100}
              falloff="linear"
              fromFontVariationSettings="'wght' 400, 'opsz' 16"
              toFontVariationSettings="'wght' 700, 'opsz' 32"
            />
          </h2>
        </div>

        {/* Grid de cards interativos */}
        <AnimatedContent
          distance={150}
          direction="vertical"
          reverse={false}
          duration={1.4}
          ease="power3.out"
          initialOpacity={0.2}
          animateOpacity
          scale={0.1}
          threshold={0.2}
          delay={0.3}
        >
          <MagicBento 
            textAutoHide={false}
            enableStars={true}
            enableSpotlight={true}
            enableBorderGlow={true}
            enableTilt={true}
            enableMagnetism={true}
            clickEffect={true}
            spotlightRadius={200}
            particleCount={30}
            glowColor="132, 0, 255"
          />
        </AnimatedContent>
      </section>

      {/* Seção de Contato */}
      <section className="contact-section" id="contact">
        <AnimatedContent
          distance={100}
          direction="vertical"
          reverse={false}
          duration={1.2}
          ease="power2.out"
          initialOpacity={0.3}
          animateOpacity
          scale={0.2}
          threshold={0.3}
          delay={0.1}
        >
          <div className="section-header">
            <h2 className="section-title">Contact Us</h2>
          </div>
        </AnimatedContent>

        <div className="contact-grid">
          <AnimatedContent
            distance={100}
            direction="vertical"
            reverse={false}
            duration={1.2}
            ease="power2.out"
            initialOpacity={0.3}
            animateOpacity
            scale={0.2}
            threshold={0.3}
            delay={0.1}
          >
            <SpotlightCard className="contact-card">
              <div className="contact-item">
                <div className="contact-header">
                  <div className="contact-icon">
                    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M4 4H20C21.1 4 22 4.9 22 6V18C22 19.1 21.1 20 20 20H4C2.9 20 2 19.1 2 18V6C2 4.9 2.9 4 4 4Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
